import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import Book from '../components/Book';
import { removeBook, changeFilter } from '../actions/index';
import Header from '../components/Header';


export class BooksList extends Component {
  constructor(props) {
    super(props)

    this.state = {

    }
  }
  handleRemoveBook = (id) => {
    removeBook(id);
  };
  handleFilterChange = (filter) => {
    changeFilter(filter);
  };
  getVisibleBooks = (books, filter) => {
    books.filter(book => book.category === filter || filter === 'All');
  }
  visible = getVisibleBooks(books, filter);
  render() {
    return (
      <div className="container BooksList">
        <div className="mx-auto col-9 shadow-lg mt-3 ">
          <div className="p-3">
            <Header onFilterChange={handleFilterChange} />
          </div>
          {
            visible.map(book => <Book clickDelete={handleRemoveBook} key={book.id} book={book} />)
          }
          <div className="clearfix" />
        </div>

      </div>
    );
  }
}

const mapStateToProps = state => ({
  books: state.books,
  filter: state.filter,
});
const mapDispatchToProps = dispatch => ({
  removeBook: id => dispatch(removeBook(id)),
  changeFilter: filter => dispatch(changeFilter(filter)),
  loadedBook: () => dispatch(getBooks())
});
BooksList.propTypes = {
  books: PropTypes.array.isRequired,
  filter: PropTypes.string.isRequired,
  removeBook: PropTypes.func.isRequired,
  changeFilter: PropTypes.func.isRequired,
  loadedBook: PropTypes.func.isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(BooksList);
