import React from 'react';
import PropTypes from 'prop-types';

function Book({ book, clickDelete }) {
  return (
    <div className="card">
      <div className="card-body d-flex justify-content-between align-items-center mt-2">
        <div>
          <h4>{book.category}</h4>
          <h2>{book.title}</h2>
        </div>
        <div>
          <button
            type="button"
            className="btn mainColor"
            onClick={() => clickDelete(book.id)}>
            Delete
          </button>
        </div>
      </div>
    </div>
  );
}
Book.propTypes = {
  book: PropTypes.object.isRequired,
  clickDelete: PropTypes.func.isRequired,
};

export default Book;
