import axios from 'axios';

const initialState = [
  {
    id: Math.random(),
    title: 'Harry Potter',
    category: 'Kids',
  },
];
const loadedBook = async () => {
  try {
    const response = await axios.get('https://bookstore-apis.herokuapp.com//api/v1/books');
    const data = response.data.book;
    return data;
  } catch (error) {
    throw error;
  }
}
const bookReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'CREATE_BOOK': return [
      ...state,
      {
        id: Math.random(),
        title: action.title,
        category: action.category,
      },
    ];
    case 'GET_BOOKS': return {
      books: loadedBook(),

    }
    case 'REMOVE_BOOK': return state.filter(book => book.id !== action.id);
    default: return state;
  }
};

export default bookReducer;
