import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import Book from '../components/Book';
import { removeBook, changeFilter } from '../actions/index';
import Header from '../components/Header';



class BooksList extends Component {
  constructor(props) {
    super(props)
    // this.visible = this.visible.bind(this)
    this.handleFilterChange = this.handleRemoveBook.bind(this);
    this.handleFilterChange = this.handleFilterChange.bind(this);
    this.getVisibleBooks = this.getVisibleBooks.bind(this);

  }
  handleRemoveBook (id){
    removeBook(id);
  };
  handleFilterChange(filter){
    changeFilter(filter);
  };
  getVisibleBooks(books, filter) {
    books.filter(book => book.category === filter || filter === 'All');
  }


  render() {
    return (
      <div className="container BooksList">
        <div className="mx-auto col-9 shadow-lg mt-3 ">
          <div className="p-3">
            <Header onFilterChange={this.handleFilterChange} />
          </div>
          {
            this.getVisibleBooks(this.props.books, this.props.filter).map(book => <Book clickDelete={this.handleRemoveBook} key={book.id} book={book} />)
          }
          <div className="clearfix" />
        </div>

      </div>
    );
  }
}

const mapStateToProps = state => ({
    books: state.books,
    filter: state.filter
});
const mapDispatchToProps = dispatch => ({
  removeBook: id => dispatch(removeBook(id)),
  changeFilter: filter => dispatch(changeFilter(filter)),
});
BooksList.propTypes = {
  books: PropTypes.array.isRequired,
  filter: PropTypes.string.isRequired,
  removeBook: PropTypes.func.isRequired,
  changeFilter: PropTypes.func.isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(BooksList);
